mapper.select:
        select
id, agent_id, agent_name, description, channel, status, create_time, update_time
        from ai_agent


                and id = #{id}


                and agent_id = #{agentId}


                and agent_name = #{agentName}


                and description = #{description}


                and channel = #{channel}


                and status = #{status}


                and create_time = #{createTime}


                and update_time = #{updateTime}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from ai_agent


        and id = #{id}


        and agent_id = #{agentId}


        and agent_name = #{agentName}


        and description = #{description}


        and channel = #{channel}


        and status = #{status}


        and create_time = #{createTime}


        and update_time = #{updateTime}
