mapper.select:
        select
id, agent_id, client_id, sequence, create_time
        from ai_agent_flow_config


                and id = #{id}


                and agent_id = #{agentId}


                and client_id = #{clientId}


                and sequence = #{sequence}


                and create_time = #{createTime}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from ai_agent_flow_config


        and id = #{id}


        and agent_id = #{agentId}


        and client_id = #{clientId}


        and sequence = #{sequence}


        and create_time = #{createTime}
