mapper.select:
        select
id, advisor_id, advisor_name, advisor_type, order_num, ext_param, status, create_time, update_time
        from ai_client_advisor


                and id = #{id}


                and advisor_id = #{advisorId}


                and advisor_name = #{advisorName}


                and advisor_type = #{advisorType}


                and order_num = #{orderNum}


                and ext_param = #{extParam}


                and status = #{status}


                and create_time = #{createTime}


                and update_time = #{updateTime}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from ai_client_advisor


        and id = #{id}


        and advisor_id = #{advisorId}


        and advisor_name = #{advisorName}


        and advisor_type = #{advisorType}


        and order_num = #{orderNum}


        and ext_param = #{extParam}


        and status = #{status}


        and create_time = #{createTime}


        and update_time = #{updateTime}
