mapper.select:
        select
id, client_id, client_name, description, status, create_time, update_time
        from ai_client


                and id = #{id}


                and client_id = #{clientId}


                and client_name = #{clientName}


                and description = #{description}


                and status = #{status}


                and create_time = #{createTime}


                and update_time = #{updateTime}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from ai_client


        and id = #{id}


        and client_id = #{clientId}


        and client_name = #{clientName}


        and description = #{description}


        and status = #{status}


        and create_time = #{createTime}


        and update_time = #{updateTime}
