mapper.select:
        select
id, prompt_id, prompt_name, prompt_content, description, status, create_time, update_time
        from ai_client_system_prompt


                and id = #{id}


                and prompt_id = #{promptId}


                and prompt_name = #{promptName}


                and prompt_content = #{promptContent}


                and description = #{description}


                and status = #{status}


                and create_time = #{createTime}


                and update_time = #{updateTime}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from ai_client_system_prompt


        and id = #{id}


        and prompt_id = #{promptId}


        and prompt_name = #{promptName}


