mapper.select:
        select
id, mcp_id, mcp_name, transport_type, transport_config, request_timeout, status, create_time, update_time
        from ai_client_tool_mcp


                and id = #{id}


                and mcp_id = #{mcpId}


                and mcp_name = #{mcpName}


                and transport_type = #{transportType}


                and transport_config = #{transportConfig}


                and request_timeout = #{requestTimeout}


                and status = #{status}


                and create_time = #{createTime}


                and update_time = #{updateTime}


        limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from ai_client_tool_mcp


        and id = #{id}


        and mcp_id = #{mcpId}


        and mcp_name = #{mcpName}


        and transport_type = #{transportType}


        and transport_config = #{transportConfig}


        and request_timeout = #{requestTimeout}


        and status = #{status}


        and create_time = #{createTime}


        and update_time = #{updateTime}
